mapper.update:
UPDATE board
SET   title = #{title}
     ,content = #{content}
     ,writer = #{writer}
WHERE board_no = #{boardNo}

UPDATE board_reply
SET   content = #{content}
     ,writer = #{writer}
WHERE reply_no = #{replyNo}

UPDATE board
SET group_no = #{groupNo}
WHERE board_no = #{boardNo}

mapper.select:
SELECT depth_no
FROM board
WHERE board_no = #{boardNo}

SELECT group_no
FROM board
WHERE board_no = #{boardNo}

SELECT COUNT(*)
FROM board
WHERE group_no = #{groupNo}

mapper.update:
UPDATE board
SET view = view + 1
WHERE board_no = #{boardNo}
