mapper.update:
UPDATE gallery
SET   title = #{title}
     ,content = #{content}
     ,writer = #{writer}
WHERE board_no = #{boardNo}

UPDATE gallery_reply
SET   content = #{content}
     ,writer = #{writer}
WHERE reply_no = #{replyNo}

UPDATE gallery
SET group_no = #{groupNo}
WHERE board_no = #{boardNo}

mapper.select:
SELECT depth_no
FROM gallery
WHERE board_no = #{boardNo}

SELECT group_no
FROM gallery
WHERE board_no = #{boardNo}

SELECT COUNT(*)
FROM gallery
WHERE group_no = #{groupNo}

mapper.update:
UPDATE gallery
SET view = view + 1
WHERE board_no = #{boardNo}
