mapper.update:
UPDATE story
SET   title = #{title}
     ,content = #{content}
     ,writer = #{writer}
WHERE board_no = #{boardNo}

UPDATE story_reply
SET   content = #{content}
     ,writer = #{writer}
WHERE reply_no = #{replyNo}

UPDATE story
SET group_no = #{groupNo}
WHERE board_no = #{boardNo}

mapper.select:
SELECT depth_no
FROM story
WHERE board_no = #{boardNo}

SELECT group_no
FROM story
WHERE board_no = #{boardNo}

SELECT COUNT(*)
FROM story
WHERE group_no = #{groupNo}

mapper.update:
UPDATE story
SET view = view + 1
WHERE board_no = #{boardNo}
